settings.custom_css:
table#listable-table {
    width: 100%;
    margin: auto;
}

div#listable-container {
    max-width: 95%;
    margin: auto;
}

table#listable-table thead.listable-header {
    background: #9e9e9e38;
}

table#listable-table thead.listable-header th {
    padding: 10px;
    font-weight: 800;
    font-family: monospace;
}

table#listable-table td {
    padding:10px;
    border: 1px solid #eaeaea;
}

td.listable-row-checkout {
    display: none;
}

th.listable-header-checkout {
    display: none;
}

div#listable-container > h2:after {
    content: 'Reserve Order';
    opacity: 1;
    font-size: 28px;
    font-family: Helvatica;
}

div#listable-container > h2 {
    font-size: 0; 0;
}
